  L.particles.forEach(p => { ctx.globalAlpha = Math.max(0, Math.min(1, p.life/60)); ctx.fillStyle = p.color || "orange"; ctx.fillRect(p.x, p.y, 3, 3); ctx.globalAlpha = 1; });

  // player
