  /* --------------------------------------------------------
     Performance Overlay (FPS + memory estimate)
     -------------------------------------------------------- */
  GP2.perf = GP2.perf || {
    last: U.now(),
    fps: 0,
    frames: 0,
    next: U.now() + 500,
  };

  GP2.register({
    postDraw() {
      if (!GP2.settings.perfOverlay) return;
      const n = U.now();
      GP2.perf.frames++;
      if (n >= GP2.perf.next) {
        const dt = n - GP2.perf.last;
        GP2.perf.fps = dt > 0 ? (GP2.perf.frames * 1000 / dt) : 0;
        GP2.perf.frames = 0;
        GP2.perf.last = n;
        GP2.perf.next = n + 500;
      }
      ctx.save();
      ctx.globalAlpha = 0.9;
      ctx.fillStyle = "rgba(255,255,255,0.72)";
      ctx.fillRect(canvas.width - 130, 10, 120, 44);
      ctx.fillStyle = "rgba(0,0,0,0.85)";
      ctx.font = "12px Arial";
      ctx.fillText(`FPS: ${GP2.perf.fps.toFixed(1)}`, canvas.width - 122, 28);
      let mem = "--";
      try {
        // Chrome-only; best-effort
        const m = performance?.memory?.usedJSHeapSize;
        if (m) mem = (m / (1024*1024)).toFixed(1) + "MB";
      } catch (e) {}
      ctx.fillText(`MEM: ${mem}`, canvas.width - 122, 44);
      ctx.restore();
    }
  });

  /* --------------------------------------------------------
     Codex (Huge content, lazily parsed, scrollable panel)
     -------------------------------------------------------- */
  GP2.codex = GP2.codex || {
    unlocked: GP2.storage.get("codex_unlocked", {}),
    _lines: null,
    _index: null,
  };

  function codexBuildIndex(lines) {
    // Map lowercased tokens -> list of line indexes (tiny inverted index).
    const idx = Object.create(null);
    for (let i=0; i<lines.length; i++) {
      const line = lines[i];
      const words = line.toLowerCase().split(/[^a-z0-9]+/g).filter(Boolean);
      const seen = new Set();
      for (const w of words) {
        if (w.length < 3) continue;
        if (seen.has(w)) continue;
        seen.add(w);
        (idx[w] ||= []).push(i);
      }
    }
    return idx;
  }

  function codexGetLines() {
    if (GP2.codex._lines) return GP2.codex._lines;
    // Split lazily only when the codex is opened.
    GP2.codex._lines = CODEX_TEXT.trim().split(/\n/);
    return GP2.codex._lines;
  }

  function codexEnsureIndex() {
    if (GP2.codex._index) return GP2.codex._index;
    GP2.codex._index = codexBuildIndex(codexGetLines());
    return GP2.codex._index;
  }

  function codexSearch(q) {
    q = (q || "").toLowerCase().trim();
    const lines = codexGetLines();
    if (!q) return { lines, hits: null };
    const idx = codexEnsureIndex();
    const parts = q.split(/\s+/).filter(Boolean);
    let hitSet = null;
    for (const p of parts) {
      const arr = idx[p] || [];
      const s = new Set(arr);
      if (hitSet == null) hitSet = s; else {
        // intersect
        const next = new Set();
        hitSet.forEach(v => { if (s.has(v)) next.add(v); });
        hitSet = next;
      }
    }
    const hits = hitSet ? Array.from(hitSet).sort((a,b)=>a-b) : [];
    const filtered = hits.map(i => lines[i]);
    return { lines: filtered, hits };
  }

  // Scroll controls: wheel + touch drag
  let _touchStartY = null;
  canvas.addEventListener("wheel", (e) => {
    if (GP2.ui.mode !== "codex") return;
    e.preventDefault();
    const dy = (e.deltaY || 0);
    GP2.ui.scroll += dy;
  }, { passive: false });

  canvas.addEventListener("touchstart", (e) => {
    if (GP2.ui.mode !== "codex") return;
    const t = e.touches && e.touches[0];
    if (!t) return;
    _touchStartY = t.clientY;
  }, { passive: true });

  canvas.addEventListener("touchmove", (e) => {
    if (GP2.ui.mode !== "codex") return;
    const t = e.touches && e.touches[0];
    if (!t || _touchStartY == null) return;
    const dy = _touchStartY - t.clientY;
    GP2.ui.scroll += dy * 1.1;
    _touchStartY = t.clientY;
  }, { passive: true });

  canvas.addEventListener("touchend", () => { _touchStartY = null; }, { passive: true });

  // Unlock codex entries when collecting secret letters (best-effort scan).
  GP2._lastLetterCount = 0;
  GP2.register({
    postUpdate() {
      if (typeof levels === "undefined" || typeof levelIndex === "undefined") return;
      const L = levels[levelIndex];
      if (!L || !Array.isArray(L.secrets)) return;
      const got = L.secrets.filter(s => s && s.kind === "letter" && s.collected).length;
      if (got !== GP2._lastLetterCount) {
        GP2._lastLetterCount = got;
        // Unlock a matching codex page number (fun reward).
        const id = `LETTER_${got}`;
        if (!GP2.codex.unlocked[id]) {
          GP2.codex.unlocked[id] = true;
          GP2.storage.set("codex_unlocked", GP2.codex.unlocked);
          try { GP.toast && GP.toast(`Codex unlocked: Page ${got}`, { life: 200 }); } catch (e) {}
        }
      }
    }
  });

  function drawCodexPanel() {
    const w = Math.min(canvas.width - 40, 780);
    const h = Math.min(canvas.height - 60, 520);
    const x = (canvas.width - w) / 2;
    const y = (canvas.height - h) / 2;

    const q = GP2.ui.search || "";
    const res = codexSearch(q);
    const lines = res.lines;

    const headerH = 76;
    const bodyY = y + headerH;
    const bodyH = h - headerH - 22;
    const rowH = GP2.settings.bigHud ? 24 : 18;
    const maxScroll = Math.max(0, lines.length * rowH - bodyH);
    GP2.ui.scroll = U.clamp(GP2.ui.scroll, 0, maxScroll);

    ctx.save();
    ctx.globalAlpha = 0.98;
    ctx.fillStyle = "rgba(255,255,255,0.92)";
    ctx.fillRect(x, y, w, h);
    ctx.strokeStyle = "rgba(0,0,0,0.18)";
    ctx.strokeRect(x, y, w, h);

    ctx.fillStyle = "rgba(0,0,0,0.88)";
    ctx.font = GP2.settings.bigHud ? "24px Arial" : "18px Arial";
    ctx.fillText("CODEX", x + 18, y + 30);

    ctx.font = GP2.settings.bigHud ? "14px Arial" : "12px Arial";
    ctx.fillText("C: close • /: search • Wheel/drag: scroll • Tip: unlock pages by finding letters", x + 18, y + 54);

    const label = q ? `Search: ${q} (${lines.length} hits)` : `Entries: ${lines.length}`;
    ctx.fillText(label, x + 18, y + 70);

    // Clip body
    ctx.save();
    ctx.beginPath();
    ctx.rect(x + 14, bodyY, w - 28, bodyH);
    ctx.clip();

    ctx.font = GP2.settings.bigHud ? "16px monospace" : "13px monospace";
    const startRow = Math.floor(GP2.ui.scroll / rowH);
    const yOffset = -(GP2.ui.scroll % rowH);
    const rowsOnScreen = Math.ceil(bodyH / rowH) + 2;
    for (let r=0; r<rowsOnScreen; r++) {
      const idx = startRow + r;
      if (idx >= lines.length) break;
      const ly = bodyY + yOffset + r * rowH + (GP2.settings.bigHud ? 20 : 16);
      const line = lines[idx];
      ctx.fillStyle = "rgba(0,0,0,0.86)";
      ctx.fillText(line, x + 20, ly);
    }
    ctx.restore();

    // Scroll bar
    if (maxScroll > 0) {
      const barX = x + w - 12;
      const barY = bodyY;
      const barH = bodyH;
      const knobH = Math.max(28, barH * (barH / (lines.length * rowH)));
      const knobY = barY + (barH - knobH) * (GP2.ui.scroll / maxScroll);
      ctx.fillStyle = "rgba(0,0,0,0.12)";
      ctx.fillRect(barX, barY, 6, barH);
      ctx.fillStyle = "rgba(0,0,0,0.35)";
      ctx.fillRect(barX, knobY, 6, knobH);
    }

    ctx.restore();
  }

  GP2.register({
    postDraw() {
      if (GP2.ui.mode !== "codex") return;
      // Slight dim behind the panel.
      ctx.save();
      ctx.fillStyle = "rgba(0,0,0,0.28)";
      ctx.fillRect(0,0,canvas.width,canvas.height);
      ctx.restore();
      drawCodexPanel();
    }
  });

  /* --------------------------------------------------------
     Extra Achievements + Quests (non-breaking registration)
     -------------------------------------------------------- */
  function registerExtras() {
    // Add achievements if the previous add-on exists.
    if (GP.ach && typeof GP.ach.register === "function") {
      const extraAch = [];
      for (let i=1; i<=120; i++) {
        extraAch.push({
          id: `ACH_ULTRA_${String(i).padStart(3,"0")}`,
          name: `Ultra Badge ${i}`,
          desc: `Complete ultra milestone #${i}.`,
          type: (i % 5 === 0) ? "levels" : (i % 3 === 0 ? "kills" : "coins"),
          threshold: (i % 5 === 0) ? Math.min(50, 5 + i) : (i % 3 === 0 ? 10*i : 25*i),
        });
      }
      GP.ach.register(extraAch);
    }

    if (GP.quests && Array.isArray(GP.quests.pool)) {
      const extraQ = [];
      for (let i=1; i<=90; i++) {
        extraQ.push({
          id: `Q_ULTRA_${String(i).padStart(3,"0")}`,
          name: `Ultra Quest ${i}`,
          desc: `Do something epic (${i}).`,
          type: (i % 4 === 0) ? "distance" : (i % 3 === 0 ? "jumps" : (i % 2 === 0 ? "kills" : "coins")),
          threshold: (i % 4 === 0) ? 1200 + i*120 : (i % 3 === 0 ? 20 + i*2 : (i % 2 === 0 ? 5 + i : 15 + i*2)),
          rewardCoins: 10 + (i % 7) * 5,
          rewardScore: 50 + i * 10,
          weight: 1,
        });
      }
      GP.quests.pool.push(...extraQ);
    }
  }

  // Run once after a tick so GP.ach/GP.quests are definitely defined.
  setTimeout(registerExtras, 0);

  /* --------------------------------------------------------
     Safety: expose a small API for debugging / future add-ons
     -------------------------------------------------------- */
  GP2.api = GP2.api || {};
  GP2.api.exportUltraSave = function() {
    const blob = {
      ultraSettings: GP2.settings,
      ultraBestMs: GP2.speedrun.bestTotalMs,
      ultraBestSplits: GP2.speedrun.bestSplits,
      ultraReplayBest: GP2.replay.best,
      ultraCodexUnlocked: GP2.codex.unlocked,
    };
    return U.safeJSONStringify(blob, "{}");
  };

  GP2.api.importUltraSave = function(jsonStr) {
    const obj = U.safeJSONParse(jsonStr, null);
    if (!obj || typeof obj !== "object") return false;
    try {
      if (obj.ultraSettings) { GP2.settings = Object.assign({}, ULTRA_DEFAULTS, obj.ultraSettings); GP2.storage.set("settings", GP2.settings); }
      if (obj.ultraBestMs != null) { GP2.speedrun.bestTotalMs = obj.ultraBestMs; GP2.storage.set("best_total_ms", obj.ultraBestMs); }
      if (obj.ultraBestSplits) { GP2.speedrun.bestSplits = obj.ultraBestSplits; GP2.storage.set("best_splits", obj.ultraBestSplits); }
      if (obj.ultraReplayBest) { GP2.replay.best = obj.ultraReplayBest; GP2.storage.set("replay_best", obj.ultraReplayBest); }
      if (obj.ultraCodexUnlocked) { GP2.codex.unlocked = obj.ultraCodexUnlocked; GP2.storage.set("codex_unlocked", obj.ultraCodexUnlocked); }
      return true;
    } catch (e) { return false; }
  };

  // Convenience hotkeys: E export, I import (prompts)
  document.addEventListener("keydown", (e) => {
    if (!e || isTypingInInput(e)) return;
    if (e.code === "KeyE" && (e.ctrlKey || e.metaKey)) {
      const data = GP2.api.exportUltraSave();
      window.prompt("Copy your ULTRA save JSON:", data);
    }
    if (e.code === "KeyI" && (e.ctrlKey || e.metaKey)) {
      const json = window.prompt("Paste ULTRA save JSON to import:", "");
      if (json != null) {
        const ok = GP2.api.importUltraSave(json);
        try { GP.toast && GP.toast(ok ? "Imported ULTRA save" : "Import failed", { life: 220 }); } catch (er) {}
      }
    }
  });

