  // Pick a curated set of rooms for the campaign worlds.
  // World 2: Forest (levels 11-20), World 3: Ice (levels 21-30), World 4: Sky (levels 31-40)
  (function buildCampaignFromRooms(){
    try{
      const byTheme = { forest:[], ice:[], sky:[], tech:[], cave:[] };
      GP3.ROOMS.forEach(r=>{ if (byTheme[r.theme]) byTheme[r.theme].push(r); else byTheme.forest.push(r); });

      function take(list, n, minDiff, maxDiff){
        const pick = list.filter(r=>r.difficulty>=minDiff && r.difficulty<=maxDiff);
        // stable shuffle based on id
        pick.sort((a,b)=>a.id.localeCompare(b.id));
        return pick.slice(0, n);
      }

      const w2 = take(byTheme.forest, 10, 2, 6);
      const w3 = take(byTheme.ice,   10, 5, 10);
      const w4 = take(byTheme.sky,   10, 8, 14);

      GP3.CAMPAIGN_ROOMS.length = 0;
      w2.concat(w3).concat(w4).forEach(r=>GP3.CAMPAIGN_ROOMS.push(r));
    }catch(e){}
  })();

